import React from 'react';
import PropTypes from 'prop-types';
import { Container, Typography, Box, Avatar, List, ListItem, ListItemAvatar, ListItemText, Paper, Grid } from '@mui/material';
import { FaYoutube, FaTwitch, FaXbox, FaSteam, FaSpotify, FaReddit, FaTwitter, FaFacebook, FaInstagram } from 'react-icons/fa';

function Profile({ user }) {
  if (!user) {
    return <Container sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Typography variant="h6">Please log in to view your profile.</Typography>
    </Container>;
  }

  const accentColor = user.accentColor ? `#${user.accentColor.toString(16)}` : '#7289da';

  return (
    <Container sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 3, mb: 4, position: 'relative' }}>
        {user.banner && (
          <Box
            component="img"
            src={`https://cdn.discordapp.com/banners/${user.discordId}/${user.banner}.png?size=1024`}
            alt="User Banner"
            sx={{
              width: '100%',
              height: '200px',
              objectFit: 'cover',
              position: 'absolute',
              top: 0,
              left: 0,
              zIndex: 1,
              borderRadius: '4px 4px 0 0'
            }}
          />
        )}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4, position: 'relative', zIndex: 2, mt: '150px' }}>
          <Avatar 
            src={`https://cdn.discordapp.com/avatars/${user.discordId}/${user.avatar}.png`} 
            alt="User Avatar" 
            sx={{ width: 100, height: 100, mr: 2, border: `4px solid ${accentColor}`, borderRadius: '50%' }}
          />
          <Box>
            <Typography variant="h4" sx={{ color: accentColor }}>{user.username}#{user.discriminator}</Typography>
            <Typography variant="body1">{user.email}</Typography>
          </Box>
        </Box>
        <Typography variant="h6" sx={{ color: accentColor }}>Profile Details</Typography>
        <List>
          <ListItem>
            <ListItemText primary="Discord ID" secondary={user.discordId} />
          </ListItem>
          <ListItem>
            <ListItemText primary="Locale" secondary={user.locale} />
          </ListItem>
          {user.premiumType !== undefined && (
            <ListItem>
              <ListItemText primary="Premium Type" secondary={getPremiumTypeName(user.premiumType)} />
            </ListItem>
          )}
          {user.flags !== undefined && (
            <ListItem>
              <ListItemText primary="Flags" secondary={getFlags(user.flags)} />
            </ListItem>
          )}
        </List>
      </Paper>
      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" sx={{ color: accentColor }}>About me!</Typography>
            <Typography variant="body1" sx={{ mt: 2 }}>
              I like video games! Lorem ipsum dolor sit amet. Et impedit quasi qui architecto optio et ipsum quia 33 earum accusamus. In debitis tempora quo facilis iusto ut sunt optio ea tenetur possimus aut omnis autem non corporis inventore.
            </Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" sx={{ color: accentColor }}>Connections</Typography>
            <List>
              {user.connections && user.connections.length > 0 ? (
                user.connections.map(connection => (
                  <ListItem key={connection.id}>
                    <ListItemAvatar>
                      {connection.type === 'youtube' && <FaYoutube />}
                      {connection.type === 'twitch' && <FaTwitch />}
                      {connection.type === 'xbox' && <FaXbox />}
                      {connection.type === 'steam' && <FaSteam />}
                      {connection.type === 'spotify' && <FaSpotify />}
                      {connection.type === 'reddit' && <FaReddit />}
                      {connection.type === 'twitter' && <FaTwitter />}
                      {connection.type === 'facebook' && <FaFacebook />}
                      {connection.type === 'instagram' && <FaInstagram />}
                      {/* Добавьте другие иконки по мере необходимости */}
                    </ListItemAvatar>
                    <ListItemText primary={connection.name} />
                  </ListItem>
                ))
              ) : (
                <Typography variant="body1">No connections available.</Typography>
              )}
            </List>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

function getPremiumTypeName(premiumType) {
  switch (premiumType) {
    case 0: return 'None';
    case 1: return 'Nitro Classic';
    case 2: return 'Nitro';
    case 3: return 'Nitro Basic';
    default: return 'Unknown';
  }
}

function getFlags(flags) {
  const flagNames = [];
  if (flags & (1 << 0)) flagNames.push('Discord Employee');
  if (flags & (1 << 1)) flagNames.push('Partnered Server Owner');
  if (flags & (1 << 2)) flagNames.push('HypeSquad Events');
  if (flags & (1 << 3)) flagNames.push('Bug Hunter Level 1');
  // Add more flag checks as needed
  return flagNames.join(', ') || 'None';
}

// Добавляем валидацию пропсов
Profile.propTypes = {
  user: PropTypes.shape({
    discordId: PropTypes.string.isRequired,
    avatar: PropTypes.string,
    username: PropTypes.string.isRequired,
    discriminator: PropTypes.string,
    email: PropTypes.string,
    locale: PropTypes.string,
    premiumType: PropTypes.number,
    flags: PropTypes.number,
    banner: PropTypes.string,
    accentColor: PropTypes.string,
    guilds: PropTypes.arrayOf(PropTypes.object),
    connections: PropTypes.arrayOf(PropTypes.object), // Updated PropTypes
  }).isRequired,
};

export default Profile;
